import React, { Component } from "react";
import { Card } from "react-bootstrap";
import SimpleText from "../input/SimpleText";
import SimpleDate from "../input/SimpleDate";
import SimpleDropdown from "../input/SimpleDropdown";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { updateStudentUI } from "../../actions/studentActions";

class PersonalInformation extends Component {
  constructor(props) {
    super(props);
    this.handlerChange = this.handlerChange.bind(this);
  }

  handlerChange(value, field) {
    this.props.updateStudentUI("personalInformation", field, value);
  }

  render() {
    const hispanic = { true: "Yes", false: "No" };

    const info = this.props.student.student.personalInformation;

    return (
      <Card.Body className="sfc-body">
        <SimpleDate label="Intake Date">info.intakeDate</SimpleDate>
        <SimpleDate label="Referral Date">info.referralDate</SimpleDate>
        <SimpleText label="Referred By">info.referredBy</SimpleText>
        <SimpleText
          label="First Name"
          handlerChange={this.handlerChange}
          field="firstName"
        >
          info.firstName
        </SimpleText>
        <SimpleText
          label="Last Name"
          handlerChange={this.handlerChange}
          field="lastName"
        >
          info.lastName
        </SimpleText>
        <SimpleText
          label="Preferred Name"
          handlerChange={this.handlerChange}
          field="preferredName"
        >
          info.preferredName
        </SimpleText>
        <SimpleText
          label="Address"
          handlerChange={this.handlerChange}
          field="address"
        >
          info.address
        </SimpleText>
        <SimpleText label="City">info.city</SimpleText>
        <SimpleText label="State">info.state</SimpleText>
        <SimpleText label="Zipcode">info.zipcode</SimpleText>
        <SimpleText label="County">info.county</SimpleText>
        <SimpleText label="Phone">info.phone</SimpleText>
        <SimpleText label="Cellphone">info.cellphone</SimpleText>
        <SimpleText label="Email">info.email</SimpleText>
        <SimpleDate label="Date of Birth">info.dateOfBirth</SimpleDate>
        <SimpleDropdown
          label="Gender"
          options={this.props.data.gender}
          activeKey={info.gender}
          placeholder="Select a gender"
        ></SimpleDropdown>
        <SimpleDropdown
          label="Ethinicity Hispanic?"
          options={hispanic}
          activeKey={info.hispanic}
        ></SimpleDropdown>
        {info.hispanic ? (
          <SimpleText label="Hispanic Specify"></SimpleText>
        ) : null}
        <SimpleDropdown
          label="Race"
          options={this.props.data.race}
          activeKey={info.race}
          placeholder="Select a race"
        ></SimpleDropdown>
        <SimpleDropdown
          label="Relationship Status"
          options={this.props.data.relationshipStatus}
          activeKey={info.relationshipStatus}
          placeholder="Select a relationship status"
        ></SimpleDropdown>
      </Card.Body>
    );
  }
}

PersonalInformation.propTypes = {
  student: PropTypes.object.isRequired,
};

PersonalInformation.defaultProps = {
  student: { PersonalInformation: {} },
};

const mapStateToProps = (state) => ({
  student: state.student,
});

const mapDispatchToProps = {
  updateStudentUI,
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(PersonalInformation);
