import React, { Component } from "react";
import PropTypes from "prop-types";
import "../../style/Student.css";
import { Dropdown } from "react-bootstrap";

export default class SimpleDropdown extends Component {
  constructor(props) {
    super(props);
    this.onSelect = this.onSelect.bind(this);
  }

  onSelect(el) {
    console.log(el.value);
  }

  render() {
    const { options } = this.props;
    const { el } = options.map((element) => (
      <Dropdown.Item activeKey={element.value}>element.label</Dropdown.Item>
    ));
    console.log(el);
    return (
      <label className="sfc-info">
        <div className="sfc-label">{this.props.label}</div>
        <Dropdown
          className="sfc-text"
          options={this.props.options}
          placeholder={this.props.placeholder}
          onChange={this.onSelect}
        >
          <Dropdown.Toggle
            id="dropdown-basic"
            className="simple-dropdown-toggle"
          >
            {"Please select an option"}
          </Dropdown.Toggle>
          <Dropdown.Menu></Dropdown.Menu>
        </Dropdown>
      </label>
    );
  }
}

SimpleDropdown.prototypes = {
  className: PropTypes.string,
  placeholder: PropTypes.string,
};

SimpleDropdown.defaultProps = {
  className: "",
  placeholder: "Select an option",
};
