import React, { Component } from "react";
import PropTypes from "prop-types";
import "../../style/Student.css";
import { Dropdown } from "react-bootstrap";

export default class SimpleDropdown extends Component {
  constructor(props) {
    super(props);
    this.onSelect = this.onSelect.bind(this);
  }

  onSelect(el) {
    console.log(el.value);
  }

  render() {
    return (
      <label className="sfc-info">
        <div className="sfc-label">{this.props.label}</div>
        <Dropdown
          className="sfc-text"
          options={this.props.options}
          placeholder={this.props.placeholder}
          onChange={this.onSelect}
        >
          <Dropdown.Toggle id="dropdown-basic">Gender</Dropdown.Toggle>
        </Dropdown>
      </label>
    );
  }
}

SimpleDropdown.prototypes = {
  className: PropTypes.string,
  placeholder: PropTypes.string,
};

SimpleDropdown.defaultProps = {
  className: "",
  placeholder: "Select an option",
};
