import React, { Component } from "react";
import { Card } from "react-bootstrap";
import SimpleText from "../input/SimpleText";
import SimpleDate from "../input/SimpleDate";
import SimpleDropdown from "../input/SimpleDropdown";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class PersonalInformation extends Component {
  render() {
    const hispanic = { false: "No", true: "Yes" };

    const { info } = this.props.student.student.personalInformation;
    return (
      <Card.Body className="sfc-body">
        <SimpleDate label="Intake Date"></SimpleDate>
        <SimpleDate label="Referral Date"></SimpleDate>
        <SimpleText label="Referred By"></SimpleText>
        <SimpleText label="First Name">info.firstName</SimpleText>
        <SimpleText label="Last Name">info.lastName</SimpleText>
        <SimpleText label="Preferred Name">info.preferredName</SimpleText>
        <SimpleText label="Address">info.address</SimpleText>
        <SimpleText label="City">info.city</SimpleText>
        <SimpleText label="State">info.state</SimpleText>
        <SimpleText label="Zipcode">info.zipcode</SimpleText>
        <SimpleText label="County">info.county</SimpleText>
        <SimpleText label="Phone">info.phone</SimpleText>
        <SimpleText label="Cellphone">info.cellphone</SimpleText>
        <SimpleText label="Email">info.email</SimpleText>
        <SimpleDate label="Date of Birth"></SimpleDate>
        <SimpleDropdown
          label="Gender"
          options={this.props.data.gender}
          activeKey={info.gender}
          placeholder="Select a gender"
        ></SimpleDropdown>
        <SimpleDropdown
          label="Race"
          options={this.props.data.race}
          placeholder="Select a race"
        ></SimpleDropdown>
        <SimpleDropdown
          label="Relationship Status"
          options={this.props.data.relationshipStatus}
          placeholder="Select a relationship status"
        ></SimpleDropdown>
      </Card.Body>
    );
  }
}

PersonalInformation.propTypes = {
  student: PropTypes.object.isRequired,
};

PersonalInformation.defaultProps = {
  student: { PersonalInformation: {} },
};

const mapStateToProps = (state) => ({
  student: state.student,
});

export default connect(mapStateToProps)(PersonalInformation);

