import axios from "axios";
import {
  GET_STUDENT,
  GET_STUDENTS,
  TOGGLE_STUDENT_SECTION,
} from "./action-types";
import { UPDATE_STUDENT_UI, ADD_STUDENT } from "./action-types";

export const getStudent = (id, data) => async (dispatch) => {
  dispatch({
    type: GET_STUDENT,
    payload: data,
  });
};

export const getStudents = () => async (dispatch) => {
  const res = await axios.get("/student/all");

  dispatch({
    type: GET_STUDENTS,
    payload: res.data,
  });
};

export const toggleStudentSection = (activeKey) => async (dispatch) => {
  dispatch({
    type: TOGGLE_STUDENT_SECTION,
    payload: activeKey,
  });
};

export const updateStudentUI = (domain, field, value) => async (dispatch) => {
  dispatch({
    type: UPDATE_STUDENT_UI,
    payload: { domain: domain, field: field, value: value },
  });
};

export const addStudent = (student, history) => async (dispatch) => {
  let studentWrapper = {};
  studentWrapper.personalInformation = student.personalInformation;
  studentWrapper.immediateNeeds = student.immediateNeeds;
  await axios.post("/student", studentWrapper);
  history.push("/dashboard");
};
