import React, { Component } from "react";
import PropTypes from "prop-types";
import "../../style/Student.css";
import { Dropdown } from "react-bootstrap";

export default class SimpleDropdown extends Component {
  constructor(props) {
    super(props);
    this.onSelect = this.onSelect.bind(this);

    this.state = {
      placeholder: "Please select an option",
      data: {},
    };
  }

  onSelect(key) {
    this.setState({ placeholder: key });
  }

  render() {
    const { options } = this.props;
    console.log(options);
    return (
      <label className="sfc-info">
        <div className="sfc-label">{this.props.label}</div>
        <Dropdown
          className="sfc-dropdown"
          options={this.props.options}
          placeholder={this.props.placeholder}
          onSelect={(key) => this.onSelect(key)}
        >
          <Dropdown.Toggle
            id="dropdown-basic"
            className="simple-dropdown-toggle"
          >
            {this.state.placeholder}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {options.map((element) => {
              this.state.data[element.id] = element.name;
              return (
                <Dropdown.Item
                  key={element.id}
                  eventKey={element.id}
                  value={element.id}
                >
                  {element.name}
                </Dropdown.Item>
              );
            })}
          </Dropdown.Menu>
        </Dropdown>
      </label>
    );
  }
}

SimpleDropdown.prototypes = {
  className: PropTypes.string,
  placeholder: PropTypes.string,
};

SimpleDropdown.defaultProps = {
  className: "",
  placeholder: "Select an option",
};
