import { GET_STUDENTS, TOGGLE_STUDENT_SECTION } from "../actions/action-types";

const initialState = {
  students: [],
  student: {},
  activeStudentSection: 3,
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_STUDENTS:
      return {
        ...state,
        students: action.payload,
      };
    case TOGGLE_STUDENT_SECTION:
      return {
        ...state,
        activeStudentSection: action.payload,
      };
    default:
      return state;
  }
}
