import React, { Component } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { BrowserRouter as Router, Route } from "react-router-dom";

import SignIn from "./components/login/SignIn";
import SignUp from "./components/login/SignUp";
import StudentForm from "./components//StudentForm";
import HeaderSideMenu from "./Layout/HeaderSideMenu";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isHeaderSideMenuVisible: true,
    };
    this.handler = this.handler.bind(this);
  }

  handler() {
    this.setState({
      isHeaderSideMenuVisible: false,
    });
  }

  render() {
    return (
      <Router>
        <div className="App">
          {this.state.isHeaderSideMenuVisible ? <HeaderSideMenu /> : null}
          <Route exact path="/signup" component={SignUp}></Route>
          <Route exact path="/signin" component={SignIn}></Route>
          <Route exact path="/studentform" component={StudentForm}></Route>
        </div>
      </Router>
    );
  }
}

export default App;

