import React, { Component } from "react";
import { Accordion, Card } from "react-bootstrap";
import SimpleText from "../input/SimpleText";
import SimpleDate from "../input/SimpleDate";
import SimpleDropdown from "../input/SimpleDropdown";

class PersonalInformation extends Component {
  render() {
    const bool = ["Yes", "No"];

    const race = [];
    const relationshipStatus = [];

    for (let id in this.props.data.race) {
      let el = {
        value: this.props.data.race[id].id,
        label: this.props.data.race[id].name,
      };
      race.push(el);
    }

    for (let id in this.props.data.relationshipStatus) {
      let el = {
        value: this.props.data.relationshipStatus[id].id,
        label: this.props.data.relationshipStatus[id].name,
      };
      relationshipStatus.push(el);
    }

    return (
      <Card.Body className="sfc-body">
        <SimpleDate label="Intake Date"></SimpleDate>
        <SimpleDate label="Referral Date"></SimpleDate>
        <SimpleText label="Referred By"></SimpleText>
        <SimpleText label="First Name"></SimpleText>
        <SimpleText label="Last Name"></SimpleText>
        <SimpleText label="Preferred Name"></SimpleText>
        <SimpleText label="Address"></SimpleText>
        <SimpleText label="City"></SimpleText>
        <SimpleText label="State"></SimpleText>
        <SimpleText label="Zipcode"></SimpleText>
        <SimpleText label="County"></SimpleText>
        <SimpleText label="Phone"></SimpleText>
        <SimpleText label="Cellphone"></SimpleText>
        <SimpleText label="Email"></SimpleText>
        <SimpleDate label="Date of Birth"></SimpleDate>
        <SimpleDropdown
          label="Gender"
          options={this.props.data.gender}
          placeholder="Select a gender"
        ></SimpleDropdown>
      </Card.Body>
    );
  }
}

export default PersonalInformation;

