import React, { Component } from "react";
import { Accordion, Card, Button } from "react-bootstrap";
import { FA_DOWN } from "../../constants/fa-icons";
import SimpleText from "../input/SimpleText";
import SimpleDate from "../input/SimpleDate";
import SimpleDropdown from "../input/SimpleDropdown";

export default class StudentFormCard extends Component {
  render() {
    const gender = [
      "Female",
      "Male",
      "Transgender-ID as Female",
      "Transgender-ID as Male",
      "Non-binary",
      "Prefer not to disclose",
    ];

    const bool = ["Yes", "No"];

    const race = [
      "Asian",
      "Black or African American",
      "Native Hawaiian/Pacific Islander",
      "White",
      "American Indian or Alaska Native",
      "Other",
    ];

    return (
      <Card className="sfc-container">
        <Card.Header className="sfc-header">
          <Accordion.Toggle as={Button} variant="link" eventKey={this.props.id}>
            {this.props.componentName}
            <i className={"fa fa-lg " + FA_DOWN} aria-hidden="true"></i>
          </Accordion.Toggle>
        </Card.Header>
        <Accordion.Collapse eventKey={this.props.id}>
          <Card.Body className="sfc-body">
            <SimpleText label="First Name"></SimpleText>
            <SimpleText label="Last Name"></SimpleText>
            <SimpleText label="Preferred Name"></SimpleText>
            <SimpleText label="Address" className="address"></SimpleText>
            <SimpleText label="City"></SimpleText>
            <SimpleText label="State"></SimpleText>
            <SimpleText label="Zipcode"></SimpleText>
            <SimpleText label="County"></SimpleText>
            <SimpleText label="Phone"></SimpleText>
            <SimpleText label="Cellphone"></SimpleText>
            <SimpleText label="Email"></SimpleText>
            <SimpleDate label="Date of Birth"></SimpleDate>
            <SimpleDropdown
              label="Gender"
              options={gender}
              placeholder="Select a gender"
            ></SimpleDropdown>
            <SimpleDropdown
              label="Ethnicity Hispanic?"
              options={bool}
            ></SimpleDropdown>
          </Card.Body>
        </Accordion.Collapse>
      </Card>
    );
  }
}
